#pragma once
#ifndef BATTERIES_CONFIG_HPP
#define BATTERIES_CONFIG_HPP

//----- --- -- -  -  -   -
// IMPORTANT: This is the only batteries header that is allowed to be included from config.hpp!
//
#include <batteries/stacktrace.hpp>
//
//----- --- -- -  -  -   -

#include <bitset>
#include <exception>
#include <iostream>

#if __cplusplus < 201703L
#error Batteries requires C++17 or later!
#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
#ifndef BATT_HEADER_ONLY
#define BATT_HEADER_ONLY 1
#endif

#if BATT_HEADER_ONLY
#define BATT_INLINE_IMPL inline
#else
#define BATT_INLINE_IMPL
#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
#define BATT_SEQ_SPECIALIZE_ALGORITHMS 0

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
// Define this preprocessor symbol to enable optional support for Google Log (GLOG).
//
//  NOTE: when using Conan to install Batteries in a downstream project, this shouldn't be defined directly;
//  instead add the following to the downstream `conanfile.py`:
//
//  ```python
//  def configure(self):
//      ...
//      self.options["batteries"].glog_support = True
//  ```
//
//#define BATT_GLOG_AVAILABLE

#ifdef BATT_GLOG_AVAILABLE
#define BATT_GLOG_SUPPORT 1
#else
#define BATT_GLOG_SUPPORT 0
#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
// Define this preprocessor symbol to enable optional support for Google Protocol Buffers (protobuf).
//
//  NOTE: when using Conan to install Batteries in a downstream project, this shouldn't be defined directly;
//  instead add the following to the downstream `conanfile.py`:
//
//  ```python
//  def configure(self):
//      ...
//      self.options["batteries"].protobuf_support = True
//  ```
//
//#define BATT_PROTOBUF_AVAILABLE

#ifdef BATT_PROTOBUF_AVAILABLE
#define BATT_PROTOBUF_SUPPORT 1
#else
#define BATT_PROTOBUF_SUPPORT 0
#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
#if defined(__GNUC__) && !defined(__clang__)

#define BATT_COMPILER_IS_GCC 1
#define BATT_IF_GCC(expr) expr

// This causes a lot of false positives, so disable.
//
#pragma GCC diagnostic ignored "-Wmaybe-uninitialized"

#else

#define BATT_COMPILER_IS_GCC 0
#define BATT_IF_GCC(expr)

#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
#if defined(__clang__)

#define BATT_COMPILER_IS_CLANG 1
#define BATT_IF_CLANG(expr) expr

#else

#define BATT_COMPILER_IS_CLANG 0
#define BATT_IF_CLANG(expr)

#endif

//==#==========+==+=+=++=+++++++++++-+-+--+----- --- -- -  -  -   -
// Enables BATT_ASSERTs explicitly (instead of inferring from -DNDEBUG)
//
//  NOTE: when using Conan to install Batteries in a downstream project, this shouldn't be defined directly;
//  instead add the following to the downstream `conanfile.py`:
