struct ThreadSpec {
    std::vector<std::vector<usize>> allowed_cpus_for_thread;

    //+++++++++++-+-+--+----- --- -- -  -  -   -
    // Convenience methods.

    usize size() const
    {
        return this->allowed_cpus_for_thread.size();
    }

    bool empty() const
    {
        return this->allowed_cpus_for_thread.empty();
    }

    const std::vector<usize>& operator[](usize i) const
    {
        return this->allowed_cpus_for_thread[i];
    }

    const std::vector<usize>& get(usize i) const
    {
        BATT_CHECK_LT(i, this->size());
        return this->allowed_cpus_for_thread[i];
    }
};

//+++++++++++-+-+--+----- --- -- -  -  -   -

inline StatusOr<ThreadSpec> parse_thread_spec(std::string in_str) noexcept
{
    ThreadSpec out_spec;
    std::istringstream iss{in_str};

    while (iss.good()) {
        std::string t_spec;
        iss >> t_spec;

        if (t_spec.empty()) {
            continue;
        }

        out_spec.allowed_cpus_for_thread.emplace_back();
        std::vector<usize>& thread_cpus = out_spec.allowed_cpus_for_thread.back();

        if (t_spec == "*") {
            thread_cpus.resize(std::thread::hardware_concurrency());
            std::iota(thread_cpus.begin(), thread_cpus.end(), 0);
        } else {
            const usize dash_pos = t_spec.find('-');
            if (dash_pos == std::string::npos) {
                usize next_comma = t_spec.find(',');
                if (next_comma == std::string::npos) {
                    const std::optional<usize> cpu_i = from_string<usize>(t_spec);
                    if (!cpu_i) {
                        return {StatusCode::kInvalidArgument};
                    }
                    thread_cpus.push_back(*cpu_i);

                } else {
                    for (;;) {
                        const std::optional<usize> cpu_i = from_string<usize>(t_spec.substr(0, next_comma));
                        if (!cpu_i) {
                            return {StatusCode::kInvalidArgument};
                        }
                        thread_cpus.push_back(*cpu_i);
                        if (next_comma == std::string::npos) {
                            break;
                        }
                        t_spec = t_spec.substr(next_comma + 1);
                        next_comma = t_spec.find(',');
                        if (next_comma == 0) {
                            return {StatusCode::kInvalidArgument};
                        }
                    }
                }

            } else {
                const std::optional<usize> cpu_lower = from_string<usize>(t_spec.substr(0, dash_pos));
                const std::optional<usize> cpu_upper = from_string<usize>(t_spec.substr(dash_pos + 1));
                if (!cpu_lower || !cpu_upper) {
                    return {StatusCode::kInvalidArgument};
                }
                for (usize cpu_i = *cpu_lower; cpu_i <= *cpu_upper; ++cpu_i) {
                    thread_cpus.push_back(cpu_i);
                }
            }
        }
    }

    BATT_VLOG(1) << BATT_INSPECT_RANGE(out_spec.allowed_cpus_for_thread);

    return out_spec;
}

