inline Status pin_thread_to_cpu(usize cpu_i)
{
#ifdef __linux__
    cpu_set_t mask;
    CPU_ZERO(&mask);
    CPU_SET(cpu_i, &mask);
    const int retval = sched_setaffinity(0, sizeof(mask), &mask);
    return status_from_retval(retval);
#else
    (void)cpu_i;
    return StatusCode::kUnimplemented;
#endif  //__linux__
}

template <typename IntRange>
inline Status pin_thread_to_cpu_set(const IntRange& cpu_i_set)
{
#ifdef __linux__
    cpu_set_t mask;
    CPU_ZERO(&mask);
    for (usize cpu_i : cpu_i_set) {
        CPU_SET(cpu_i, &mask);
    }
    const int retval = sched_setaffinity(0, sizeof(mask), &mask);
    return status_from_retval(retval);
#else
    (void)cpu_i_set;
    return StatusCode::kUnimplemented;
#endif  //__linux__
}

struct ThreadPoolConfig {
    usize thread_count;
    usize cpu_group_size;
    usize first_cpu;
    bool pin;
};

inline Status pin_thread_i_of_config(usize i, const ThreadPoolConfig& config,
                                     const std::string_view& pool_name = "")
{
    static const usize cpu_count = std::thread::hardware_concurrency();

    if (i >= config.thread_count) {
        return StatusCode::kInvalidArgument;
    }

    if (!config.pin) {
        return OkStatus();
    }

    // The position of this thread within its CPU group.
    //
    const usize group_position = i % config.cpu_group_size;

    // The first CPU within this thread's CPU group.
    //
    const usize group_first = i - group_position + config.first_cpu;

    // The last CPU in the group.
    //
    const usize group_last = group_first + config.cpu_group_size;

    // Build the CPU set to pin this worker thread.
    //
    SmallVec<int, 64> cpu_set;
    for (usize j = group_first; j < group_last; ++j) {
        cpu_set.emplace_back(static_cast<int>(j % cpu_count));
    }

    BATT_VLOG(1) << pool_name << "[" << i << "]: cpu " << batt::dump_range(cpu_set);

    return pin_thread_to_cpu_set(cpu_set);
}

}  // namespace batt

