#pragma once
#define BATTERIES_OBJECT_THREAD_STORAGE_HPP

#include <batteries/config.hpp>
//
#include <batteries/assert.hpp>
#include <batteries/finally.hpp>
#include <batteries/hint.hpp>
#include <batteries/int_types.hpp>

#include <memory>
#include <mutex>
#include <unordered_set>
#include <vector>

namespace batt {

//=#=#==#==#===============+=+=+=+=++=++++++++++++++-++-+--+-+----+---------------
//
template <typename T>
class ObjectThreadStorage
{
   public:
    using Self = ObjectThreadStorage;

    static constexpr usize kInvalidSlot = ~usize{0} >> 24;

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    static Self& instance()
    {
        // Intentionally leak `instance_` to avoid dtor ordering problems on shutdown.
        //
        static Self* instance_ = new Self{};

        return *instance_;
    }

    template <typename... Args>
    static T& get_slot(usize slot_i, Args&&... args)
    {
        thread_local std::vector<std::unique_ptr<T>>& slots_ = Self::slots_vector_for_thread();

        if (BATT_HINT_FALSE(slots_.size() <= slot_i)) {
            slots_.resize(slot_i + 1);
        }
        std::unique_ptr<T>& state = slots_[slot_i];

        if (!state) {
            state = std::make_unique<T>(BATT_FORWARD(args)...);
        }
        return *state;
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    // Forward-declare.
    //
    class ScopedSlot;

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    usize alloc_slot()
    {
        std::unique_lock<std::mutex> lock{this->mutex_};

        if (!this->slot_pool_.empty()) {
            const usize slot_i = this->slot_pool_.back();
            this->slot_pool_.pop_back();
            return slot_i;
        }

        const usize new_slot_i = this->total_slots_created_;
        ++this->total_slots_created_;

        BATT_CHECK_LT(new_slot_i, Self::kInvalidSlot);

        return new_slot_i;
    }

    void free_slot(usize slot_i)
    {
        if (slot_i == kInvalidSlot) {
            return;
        }
        BATT_CHECK_LT(slot_i, kInvalidSlot);

        // Instead of deleting objects with the mutex held, we transfer ownership to this local vector, then
        // destroy them all at the end.
        //
        std::vector<std::unique_ptr<T>> to_destruct;
        {
            std::unique_lock<std::mutex> lock{this->mutex_};

            for (std::vector<std::unique_ptr<T>>* per_thread : this->thread_states_) {
                BATT_CHECK_NOT_NULLPTR(per_thread);

                // If a given thread's slot vector is smaller than slot_i + 1, then this slot index was never
                // used on that thread; just continue.
                //
                if (slot_i < per_thread->size()) {
                    to_destruct.emplace_back(std::move((*per_thread)[slot_i]));
                    BATT_CHECK_EQ((*per_thread)[slot_i], nullptr);
                }
            }

            this->slot_pool_.emplace_back(slot_i);
        }
        // to_destruct's dtor runs, freeing all instances of T for the given slot.
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -
   private:
    static std::vector<std::unique_ptr<T>>& slots_vector_for_thread()
    {
        thread_local std::vector<std::unique_ptr<T>> slots_;

        thread_local bool initialized_ = [&] {
            Self& self = Self::instance();
            std::unique_lock<std::mutex> lock{self.mutex_};
            auto [iter, inserted] = self.thread_states_.emplace(std::addressof(slots_));
            BATT_CHECK(inserted);
            return true;
        }();

        thread_local auto on_scope_exit = batt::finally([&] {
            Self& self = Self::instance();
            std::unique_lock<std::mutex> lock{self.mutex_};
            self.thread_states_.erase(std::addressof(slots_));
        });

        BATT_ASSERT(initialized_);

        return slots_;
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    // Protects all state of this class against data races.
    //
    std::mutex mutex_;

    // Slot indices which were allocated but are currently available.  We want to re-use these so that the
    // per-thread vectors don't grow unbounded over time.
    //
    std::vector<usize> slot_pool_;

    // Keeps track of the minimum unused slot index value, in case `this->slot_pool_` is empty.
    //
    usize total_slots_created_ = 0;

    // Each thread registers its slot vector inside `get_slot`; we track these here so that per-object,
    // per-thread instances of T can be destructed when their `ScopedSlot` is deleted.
    //
    std::unordered_set<std::vector<std::unique_ptr<T>>*> thread_states_;
};

//=#=#==#==#===============+=+=+=+=++=++++++++++++++-++-+--+-+----+---------------
//
template <typename T>
class ObjectThreadStorage<T>::ScopedSlot
{
   public:
    static constexpr usize kInvalidSlot = ObjectThreadStorage<T>::kInvalidSlot;

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    explicit ScopedSlot() noexcept : slot_i_{ObjectThreadStorage<T>::instance().alloc_slot()}
    {
    }

    ~ScopedSlot() noexcept
    {
        ObjectThreadStorage<T>::instance().free_slot(this->slot_i_);
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    ScopedSlot(const ScopedSlot&) = delete;

    ScopedSlot& operator=(const ScopedSlot&) = delete;

    ScopedSlot(ScopedSlot&& that) noexcept : slot_i_{that.slot_i_}
    {
        that.slot_i_ = ObjectThreadStorage<T>::kInvalidSlot;
    }

    ScopedSlot& operator=(ScopedSlot&& that) noexcept
    {
        ScopedSlot tmp{std::move(that)};
        std::swap(tmp.slot_i_, this->slot_i_);
        return *this;
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -

    bool is_valid() const noexcept
    {
        return this->slot_i_ != ObjectThreadStorage<T>::kInvalidSlot;
    }

    usize index() const noexcept
    {
        return this->slot_i_;
    }

    template <typename... Args>
    T& get(Args&&... args) const
    {
        return ObjectThreadStorage<T>::get_slot(this->slot_i_, BATT_FORWARD(args)...);
    }

    //+++++++++++-+-+--+----- --- -- -  -  -   -
   private:
    // The slot index.
    //
    usize slot_i_;
};

}  // namespace batt
