#include <batteries/optional.hpp>

#include <type_traits>
#include <utility>

namespace batt {

//=#=#==#==#===============+=+=+=+=++=++++++++++++++-++-+--+-+----+---------------

template <typename T>
T&& get_typed_arg(T&& default_value)
{
    return std::forward<T>(default_value);
}

template <typename T, typename... Rest>
T&& get_typed_arg(T&& /*default_value*/, T&& first, Rest&&... /*rest*/)
{
    return std::forward<T>(first);
}

template <
    typename T, typename First, typename... Rest,
    typename = std::enable_if_t<IsOptional<T>{} && std::is_same_v<std::decay_t<First>, RemoveOptional<T>>>>
T&& get_typed_arg(T&& /*default_value*/, First&& first, Rest&&... /*rest*/)
{
    thread_local std::decay_t<T> tmp;
    tmp.emplace(std::forward<First>(first));
    return std::forward<T>(tmp);
}

template <typename T, typename First, typename... Rest,
          typename = std::enable_if_t<(!IsOptional<T>{} && !std::is_same_v<T, First>) ||
                                      !std::is_same_v<std::decay_t<First>, RemoveOptional<T>>>,
          typename = void>
T&& get_typed_arg(T&& default_value, First&& /*first*/, Rest&&... rest)
{
    return get_typed_arg<T>(std::forward<T>(default_value), std::forward<Rest>(rest)...);
}

//=#=#==#==#===============+=+=+=+=++=++++++++++++++-++-+--+-+----+---------------

template <typename T>
constexpr std::false_type has_typed_arg()
{
    return {};
}

template <typename T, typename... Rest>
constexpr std::true_type has_typed_arg(T&& /*first*/, Rest&&... /*rest*/)
{
    return {};
}

template <
    typename T, typename First, typename... Rest,
    typename = std::enable_if_t<IsOptional<T>{} && std::is_same_v<std::decay_t<First>, RemoveOptional<T>>>>
constexpr std::true_type has_typed_arg(First&& /*first*/, Rest&&... /*rest*/)
{
    return {};
}

template <typename T, typename First, typename... Rest,
          typename = std::enable_if_t<(!IsOptional<T>{} && !std::is_same_v<T, First>) ||
                                      !std::is_same_v<std::decay_t<First>, RemoveOptional<T>>>,
          typename = void>
constexpr auto has_typed_arg(First&& /*first*/, Rest&&... rest)
{
    return has_typed_arg<T>(std::forward<Rest>(rest)...);
}

}  // namespace batt
